import axios from "axios";
import React from "react";


class PostsList extends React.Component{
    state = {
        posts: []
    };

    componentDidMount() {
      const { user } = this.props;  

        axios
        .get(`https://jsonplaceholder.typicode.com/posts?userId=${user.id}`)
        .then(result => {
            this.setState({
                posts: result.data
            });
        });
    }

    componentDidUpdate(prevProps, prevState) {
        console.log("prevProps prevState", prevProps, prevState);
        console.log("props, state", this.props, this.state);
    }

    render(){
        const { posts } = this.state;
        return(
        <div>
            {posts.map(post =>{
                return (
                <div key={post.id} className="card" style={{ width: "18rem" }}>
                <div className="card-body">
                  <h5 className="card-title">{post.title}</h5>
                  <p className="card-text"> {post.body} </p>
                </div>
              </div>
            );             
            } )}
        </div>
        );
    }
}

export default PostsList;
